import '../styles/login.css';
import { useState } from 'react';
import axios from 'axios';
import { login } from '../helpers/Utils';
import Icon from '../components/Icon';
import BigShip from '../components/componentImages/BigShip.jpeg';

export default function Login() {
    const [userName, setUserName] = useState("");
    const [userPassword, setUserPassword] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        if(userName === "" || userPassword === ""){
            return window.alert("É preciso digitar um nome de usuário e uma senha");
        }

        try {
            const response = await axios.post('http://localhost:8000/api/login', {}, {
                auth: {
                    username: userName,
                    password: userPassword
                }
            });

            if(response.data.sucesso === 1) {
                login(userName, userPassword);
                window.alert("Usuário autenticado com sucesso");
            } else {
                window.alert("Erro ao autenticar usuário: \n" + response.data.erro);
            }
        } catch (error) {
            console.error("Erro na requisição:", error);
            window.alert("Erro na comunicação com o servidor: " + error.message);
        }
    };

    return (
        <div className="page">
            <div className="image-container">
                <img className="image" src={BigShip} alt="Navio Grandão" />
            </div>
            <div className="login">
                <p className="page_title">
                    <Icon /> MyPort Manager
                </p>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        className="input"
                        name="userName"
                        value={userName}
                        placeholder="Nome de Usuário"
                        onChange={(e) => setUserName(e.target.value)}
                    />
                    <input
                        type="password"
                        className="input"
                        name="userPassword"
                        value={userPassword}
                        placeholder="Senha"
                        onChange={(e) => setUserPassword(e.target.value)}
                    />
                    <button
                        type="submit"
                        className="button"
                    >Login</button>
                </form>
            </div>
        </div>
    );
}
