import '../styles/login.css';
import { useState } from 'react';
import axios from 'axios';
import { login } from '../helpers/Utils';
import Icon from '../components/IconBlack';
import BigShip from '../components/componentImages/BigShip.jpeg';

export default function Login({onLogin}) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        if(email === ""){
            window.alert("Please enter an email");
        }

        if(password === ""){
            window.alert("Please enter a password");
        }

        axios({
            method: 'post',
            url: 'http://localhost:8000/api/login',
            auth: {
                username: email,
                password: password
            }
        }).then((response) => {
            if(response.data["success"] === 1) {
                login(email, password);
                onLogin(true);
            } else {
                window.alert("Authentication error: \n" + response.data["error"]);
            }
        }).catch((error) => {
            console.error("Request error:", error);
            window.alert("Server communication error: " + error.message);
        });
    };

    return (
        <div className="page">
            <img src={BigShip} alt='Large Ship' style={{ maxWidth: '50%', height: '100vh' }}/>
            <div className="login">
                <div className="page_title">
                    <Icon /> 
                    <h2>MyPort Manager</h2>
                </div>
                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        className="input"
                        name="email"
                        value={email}
                        placeholder="Enter email"
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        className="input"
                        name="password"
                        value={password}
                        placeholder="Enter password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <a href="#" className="forgot-password-link">Forgot Password?</a>
                    <button type="submit" className="submit-button">Log in</button>
                </form>
            </div>
        </div>
    );
}
