import React from 'react';
import axios from 'axios';
import '../styles/containerdashdoard.css';
import { useState, useEffect } from 'react';
import { getUser, getPassword } from "../helpers/Utils";

function OperationDashBoard() {
    return (
        <div className="dashboard-page">
            <div className="main-content">
                <Header />
                <DashboardContent />
            </div>
        </div>
    );
}

function NavLink({ label }) {
    if (label === "Register") return <a href="register">{label}</a>;
    return <a href="test">{label}</a>;
}

function Header() {
    return (
        <div className="header">
            <input
                type="text"
                className="search-bar"
                placeholder="Search resources..."
            />
            <div className="header-icons">
                <nav className="nav-links">
                    <NavLink label="Ships Dashboard"/>
                    <NavLink label="Ships Dashboard"/>  
                    <NavLink label="Ships Dashboard"/>  
                    <NavLink label="Ships Dashboard"/>  
                </nav>
                <button className="create-button">Create Operation</button>
            </div>
        </div>
    );
}

function DashboardContent() {
    const [OperationsByShipType, setOperationsByShipType] = useState([]);

    useEffect(() => {
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getOperationPerShipType',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setOperationsByShipType(response.data["operations"]);
                    console.log(response.data["operations"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });
    }, []); 

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <h2>Operações por Tipo de Navio</h2>
            </div>
            <div className="dashboard-content">
                <SummaryCard title="Operações por Tipo de Navio" data={OperationsByShipType} />
                <RightSidebar />
            </div>
        </div>
    );
}

function SummaryCard({ title, data }) {
    return (
        <div className="summary-card">
            <h3>{title}</h3>
            {data && data.length > 0 ? (
                <div className="location-summary-table">
                    {data.map((item) => (
                        <div key={item.IdShipType} className="location-summary-row">
                            <span className="location-name">{item.ShipTypeName}</span>
                            <span className="container-count">{item.TotalOperacoes}</span>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Loading data...</p>
            )}
        </div>
    );
}

function RightSidebar() {
    const [DelayedOperations, setDelayedOperations] = useState([]);
    const [TotalOperations, setTotalOperations] = useState(null);
    const [OperationsWithoutDelay, setOperationsWithoutDelay] = useState(null);

    useEffect(() => {
        // Operações com Atraso e Duração do Atraso
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getDelayedOperations',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setDelayedOperations(response.data["delayed_operations"]);
                    console.log(response.data["delayed_operations"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });

        // Número Total de Operações
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getTotalOperations',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setTotalOperations(response.data["total_operations"]);
                    console.log(response.data["total_operations"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });

        // Percentual de Operações Sem Atraso
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getOperationsWithoutDelay',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setOperationsWithoutDelay(response.data["percentual_operations_without_delay"]);
                    console.log(response.data["percentual_operations_without_delay"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });
    }, []); 

    return (
        <div className="right-sidebar">
            <SmallCard 
                title="Operações com Atraso" 
                count={DelayedOperations.length > 0 ? DelayedOperations.length : "Loading..."} 
            />
            <SmallCard 
                title="Número Total de Operações" 
                count={TotalOperations !== null ? TotalOperations : "Loading..."} 
            />
            <SmallCard 
                title="Percentual de Operações Sem Atraso" 
                count={OperationsWithoutDelay !== null ? `${OperationsWithoutDelay}%` : "Loading..."} 
            />
        </div>
    );
}


function SmallCard({ title, count }) {
    return (
        <div className="small-card">
            <h4>{title}</h4>
            <div className="card-content">
                <div className="card-count">
                    {count !== null ? count : "Loading..."} {/* Display count or loading state */}
                </div>
                <div className="view-all">View All</div>
            </div>
        </div>
    );
}

export default ContainerDashBoard;
