import React from 'react';
import axios from 'axios';
import '../styles/containerdashdoard.css';
import { useState, useEffect } from 'react';
import { getUser, getPassword } from "../helpers/Utils";

function OperationDashBoard() {
    return (
        <div className="dashboard-page">
            <div className="main-content">
                <Header />
                <DashboardContent />
            </div>
        </div>
    );
}

function NavLink({ label }) {
    if (label === "Register") return <a href="register">{label}</a>;
    return <a href="test">{label}</a>;
}

function Header() {
    return (
        <div className="header">
            <input
                type="text"
                className="search-bar"
                placeholder="Search resources..."
            />
            <div className="header-icons">
                <nav className="nav-links">
                    <NavLink label="Ships Dashboard"/>
                    <NavLink label="Ships Dashboard"/>  
                    <NavLink label="Ships Dashboard"/>  
                    <NavLink label="Ships Dashboard"/>  
                </nav>
                <button className="create-button">Create Container</button>
            </div>
        </div>
    );
}

function DashboardContent() {
    const [OperationsByShipType, setOperationsByShipType] = useState([]);
    const [DelayedOperations, setDelayedOperations] = useState([]);

    useEffect(() => {
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getOperationsbyShip',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setOperationsByShipType(response.data["operations"]);
                    console.log(response.data["operations"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });
        //=============================================================
        axios({
            method: 'get',
            url: 'http://localhost:8000/api/getOperationsDelays',
            params: {},
            auth: {
                username: getUser(),
                password: getPassword()
            },
            headers: {'Content-Type': 'application/x-www-form-urlencoded'}
        })
        .then((response) => {
            if(response.status === 200) {
                if(response.data["sucesso"] === 1) {
                    setDelayedOperations(response.data["operations"]);
                    console.log(response.data["operations"]);
                } else {
                    console.error("Error fetching data:", response.data["erro"]);
                }
            } else {
                window.alert("Error fetching data from the server.");
            }
        })
        .catch((error) => {
            console.error("Axios error:", error);
            window.alert("An error occurred while fetching data.");
        });
    }, []); 

    return (
        <div className="dashboard">
            <div className="dashboard-header">
                <h2>Container Dashboard</h2>
            </div>
            <div className="dashboard-content">
                <SummaryCard title="Container Location Summary" data={OperationsByShipType} />
                <SummaryCard title="Container Type Summary" data={DelayedOperations}/>
                <RightSidebar />
            </div>
        </div>
    );
}

function SummaryCard({ title, data }) {
    return (
        <div className="summary-card">
            <h3>{title}</h3>
            {data && data.length > 0 ? (
                <div className="location-summary-table">
                    {data.map((item) => (
                        <div key={item.idlocation} className="location-summary-row">
                            <span className="location-name">{item.modelname || item.idoperation}</span>
                            <span className="container-count">{item.totaloperacoes || item.duracaoatraso.minutes}</span>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Loading data...</p>
            )}
        </div>
    );
}

